import { Component } from "react";

class Medicine extends Component {
  constructor(props) {
    super(props);
  }

  componentDidMount() {
    this.getMedicine(this.props.router.params.id);
  }

  onChangeName(e) {
    const name = e.target.name;

    this.setState(function (prevState) {
      return {
        currentMedicine: {
          ...prevState.currentMedicine,
          name: name,
        },
      };
    });
  }
}
